sp_widget.script:
(function($sp, input, data, options, gs) {
data.sys_id = $sp.getParameter("sys_id");
	var gr = new GlideRecord('x_ssnbv_symcode_package');
	gr.addQuery('sys_id', data.sys_id);
	gr.query();
	if(gr.next())
		{
			data.name = gr.name.getDisplayValue();
			data.latest_version = gr.latest_version.getDisplayValue();	
			data.compatibility = gr.compatibility.getDisplayValue();
			data.category = gr.category.getDisplayValue();
		}
	var filenames = [];
	var attachment = new GlideRecord('sys_attachment');
	attachment.addEncodedQuery('table_name=x_ssnbv_symcode_package^table_sys_id='+data.sys_id);
	attachment.orderByDesc('sys_created_on');
	attachment.query();
	//gs.addErrorMessage(attachment.getRowCount());
	while(attachment.next())
		{
			var a = {};
			a.filename = attachment.file_name.toString();
			a.fileid = attachment.sys_id.toString();
			filenames.push(a);
			//data.file_name = attachment.file_name.toString();
		}
	data.file_name = filenames;
})($sp, input, data, options, gs);
