sp_widget.script:
(function() {

	// Get records
	data.records = [];
	var gr = new GlideRecord(options.table);
	if ($sp.getParameter('filter')) gr.addEncodedQuery($sp.getParameter('filter'));
	if (options.filter) gr.addEncodedQuery(options.filter);
	if (options.maximum_entries) gr.setLimit(options.maximum_entries);
	if (options.order_by && options.order_direction != 'desc') gr.orderBy(options.order_by);
	if (options.order_by && options.order_direction == 'desc') gr.orderByDesc(options.order_by);
	gr.query();
	while (gr.next()) {
		data.records.push({
			'sys_id': gr.getUniqueValue(),
			'table': gr.getTableName(),
			'label': (options.display_field ? gr[options.display_field].getDisplayValue() : gr.getDisplayValue()),
			'description': (options.description_field ? gr[options.description_field].getDisplayValue() : ''),
			'html': (options.html_field ? gr[options.html_field].getDisplayValue() : ''),
			'picture': (options.picture_field ? gr[options.picture_field].getDisplayValue() : ''),
		});
	}

})();
