sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var version = 0,
        count = 0;
    var pkg = new GlideRecord('x_ssnbv_symcode_package');
    pkg.addQuery('active', true);
    pkg.query();
    while (pkg.next()) {
        version = 0;
        var pkgVersion = new GlideRecord('x_ssnbv_symcode_available_package_version');
        pkgVersion.addQuery('version.package', pkg.sys_id);
        pkgVersion.addQuery('instance', current.sys_id);
        pkgVersion.query();
        while (pkgVersion.next())
            version++;

        if (version == 0) {
            // latest version - x_ssnbv_symcode_package_version
            var pkgVer = new GlideRecord('x_ssnbv_symcode_package_version');
            pkgVer.addQuery('package', pkg.sys_id);
            pkgVer.orderByDesc('released_on');
            pkgVer.setLimit(1);
            pkgVer.query();
            if (pkgVer.next()) {
                var newVersion = new GlideRecord('x_ssnbv_symcode_available_package_version');
                newVersion.instance = current.sys_id;
                newVersion.version = pkgVer.sys_id; // latest version for this package
                newVersion.insert();
                count++;
            }
        }
    }
    if (count > 0)
        gs.addInfoMessage("Instance has added to all active packages.");

})(current, previous);
