sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

          // Add your code here
	var ins="";
	var pack="";
	var ver ="";
	var own="";
	var rec = new GlideRecord("x_ssnbv_symcode_installed_package_version");
	rec.addEncodedQuery('version.state=released^versionISNOTEMPTY^instanceISNOTEMPTY');
	rec.addQuery('version.package',event.parm1);
	rec.query();
	template.print('<p>Hello,</p><p></p><p>The table below identifies the <strong>Packages</strong> where you are identified as the <strong>Owner</strong> and contains details of the <strong>Instances</strong> and <strong>their Owners</strong> where the <strong>Packages</strong> have been deployed.: </p><p>&nbsp;</p>');
	template.print('<table style="border-collapse: collapse; width: 100%; height: 13px;" border="1"><tbody><tr style="height: 13px;"><td style="width: 25%; height: 13px;"><strong>Package</strong></td><td style="width: 25%; height: 13px;"><strong>Version</strong></td><td style="width: 25%; height: 13px;"><strong>Instance</strong></td><td style="width: 25%; height: 13px;"><strong>Instance Owner</strong></td></tr></tbody></table>');
	while(rec.next())
		{
			ins= rec.getDisplayValue('instance');
			pack= rec.getDisplayValue('version.package');
			ver = rec.getDisplayValue('version.version');
			own= rec.getDisplayValue('instance.owner');

			template.print('<table style="border-collapse: collapse; width: 100%; height: 13px;" border="1"><tbody><tr style="height: 13px;"><td style="width: 25%; height: 13px;">'+pack+'</td><td style="width: 25%; height: 13px;">'+ver+'</td><td style="width: 25%; height: 13px;">'+ins+'</td><td style="width: 25%; height: 13px;">'+own+'</td></tr></tbody></table>');
		}

	//template.print('<p>Hello,</p><p>&nbsp;</p><p>Please find the instances installed your package.</p><table style="border-collapse: collapse; width: 99.8478%; height: 73px;" border="1"><tbody><tr style="height: 15px;"><td style="width: 25%; height: 15px;"><strong>Package</strong></td><td style="width: 25%; height: 15px;">&nbsp;<strong><a tabindex="0" title="" role="button" data-original-title="Please provide the version of package" data-list_id="x_ssnbv_symcode_installed_package_version" data-type="list2_hdrcell">Version</a> </strong></td><td style="width: 25%; height: 15px;"><strong><a tabindex="0" title="" role="button" data-original-title="" data-list_id="x_ssnbv_symcode_installed_package_version" data-type="list2_hdrcell">Instance</a> </strong></td><td style="width: 25%; height: 15px;"><strong><a tabindex="0" title="" role="button" data-original-title="" data-list_id="x_ssnbv_symcode_installed_package_version" data-type="list2_hdrcell">Instance Owner</a></strong></td></tr><tr style="height: 13px;"><td style="width: 25%; height: 13px;">'+pack+'</td><td style="width: 25%; height: 13px;">'+ver+'</td><td style="width: 25%; height: 13px;">'+ins+'</td><td style="width: 25%; height: 13px;">'+own+'</td></tr></tbody></table><p>&nbsp;</p><p>Thanks,</p><p>Symcode Team</p>');

})(current, template, email, email_action, event);
