sys_script_fix.script:
var instance = 'ab91ccb71bfe205070f8a868b04bcb21'; //dev79886
var version = 0;
var pkg = new GlideRecord('x_ssnbv_symcode_package');
pkg.addQuery('active', true);
pkg.query();
while (pkg.next()) {
    //gs.debug('name: ' + pkg.name, 'SymCode');
    //check the instance added to the release or not
    version = 0;
    var pkgVersion = new GlideRecord('x_ssnbv_symcode_available_package_version');
    pkgVersion.addQuery('version.package', pkg.sys_id);
    pkgVersion.addQuery('instance', 'ab91ccb71bfe205070f8a868b04bcb21');
    pkgVersion.query();
    while (pkgVersion.next()) {
        //gs.debug('Package version: ' + pkgVersion.version.name + ', instance: ' + pkgVersion.instance.name, 'SymCode');
        //add to the list
        version = version + 1;
    }
    if (version == 0) {
        gs.debug('Package version: ' + pkg.name, 'SymCode');

        // latest version - x_ssnbv_symcode_package_version
        var pkgVer = new GlideRecord('x_ssnbv_symcode_package_version');
        pkgVer.addQuery('package', pkg.sys_id);
        pkgVer.orderByDesc('released_on');
        pkgVer.setLimit(1);
        pkgVer.query();
        if (pkgVer.next()) {
            var newVersion = new GlideRecord('x_ssnbv_symcode_available_package_version');
            newVersion.instance = instance;
            newVersion.version = pkgVer.sys_id; // latest version for this package
            newVersion.insert();
        }
    }
}
gs.debug('Gee - Insert into package versions - END', 'SymCode');
