sys_script_include.script:
var SymcodeInstalledtoVersion = Class.create();
SymcodeInstalledtoVersion.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    loadpage: function() {
        var userId = this.getParameter('userId');
        var gr = new GlideRecord('x_ssnbv_symcode_credential');
        gr.addQuery('user', userId);
        gr.addQuery('instance.active', true);
        gr.query();
        while (gr.next()) {
            try {
                var util = new SymCodeInstanceUtil(gr.instance.toString());
                util.refreshInstalledVersions();
                util.refreshAvailableVersions();
            } catch (ex) {}
        }
        //action.setRedirectURL(current);
        return true;
    },

    type: 'SymcodeInstalledtoVersion'
});
