sysauto_script.script:
loadpage();

function loadpage() {
    var gr = new GlideRecord('x_ssnbv_symcode_credential');
    gr.addQuery('user', gs.getUserID());
    gr.addQuery('instance.active', true);
    gr.query();
    gs.info("Satish test Business rule 1 -" + gs.getUserID());
    while (gr.next()) {
        try {
            gs.info("Satish test Business rule 1 in try -");
            var util = new SymCodeInstanceUtil(gr.instance.toString());
            gs.info("Satish test Business rule 1 in try - 1");
            util.refreshInstalledVersions();
            gs.info("Satish test Business rule 1 in try - 2");
            util.refreshAvailableVersions();
            gs.info("Satish test Business rule try end -");
        } catch (ex) {}
    }
    //action.setRedirectURL(current);
}
